extra_data.question2sql:
SELECT product_id FROM `leggings` WHERE thickness != 'medium' 
AND (brand = 'Hollywood Star Fashion' OR brand = 'Ruthy\'s Apparel')
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `leggings` WHERE thickness = 'medium' 
AND brand != 'ToBeInStyle' 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `leggings` WHERE warmth = 'cool' OR warmth = 'moderate'
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `leggings` WHERE brand IN ('Yelete', 'Ayurvastram')
AND thickness = 'thick'
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `leggings` WHERE brand != 'ToBeInStyle' AND transparency = 'opaque' 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `leggings` WHERE brand IN ('QZUnique', 'Ayurvastram') 
AND thickness <> 'thick' 
ORDER BY price LIMIT 1;

SELECT product_id FROM `leggings` WHERE brand != 'Liang Rou' 
AND thickness = 'thick' 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `leggings` WHERE brand != 'Dimore' 
AND warmth != 'hot' 
ORDER BY price LIMIT 1;
