extra_data.question2sql:
SELECT product_id FROM `digital_scales` WHERE warranty >= (SELECT AVG(warranty) FROM digital_scales)
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE brand != 'My Weigh'
ORDER BY price
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE brand IN ('My Weigh', 'Ozeri') 
ORDER BY warranty DESC 
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE battery_type = 'AAA'
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE battery_type <> 'AA' 
ORDER BY price 
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE 
    (brand = 'Deco Brothers' OR brand = 'Keepax') 
    AND battery_type != 'AAA';

SELECT product_id FROM `digital_scales` WHERE battery_type != 'AA'
ORDER BY warranty DESC
LIMIT 1;

SELECT product_id FROM `digital_scales` WHERE price < (SELECT AVG(price) FROM digital_scales)
ORDER BY warranty DESC
LIMIT 1;
