extra_data.question2sql:
SELECT product_id FROM `dresses` WHERE dress_length = 'knee-length' 
AND neck_style = 'polo' 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `dresses` WHERE brand IN ('Memorose', 'Allegrace') 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `dresses` WHERE price = (SELECT price FROM dresses ORDER BY price LIMIT 1) 
AND (neck_style = 'V-neck' OR neck_style = 'collared') 
AND (closure_type = 'elasticity' OR closure_type = 'zipper') LIMIT 1;

SELECT product_id FROM `dresses` WHERE brand IN ('Urban CoCo', 'On Trend') 
AND dress_length!='midi' 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `dresses` WHERE brand IN ('Kate Kasin', 'G2 Chic') 
AND dress_length IN ('midi', 'knee-length') 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `dresses` WHERE (dress_length = 'long' OR dress_length = 'short') 
AND (neck_style = 'polo' OR neck_style = 'V-neck') 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `dresses` WHERE closure_type IN ('button', 'belted')
AND dress_length IN ('knee-length', 'midi')
LIMIT 1;
