extra_data.question2sql:
SELECT product_id FROM `headphones` WHERE connectivity = 'wired' 
AND (headphone_type = 'in-ear' OR headphone_type = 'over-ear') 
ORDER BY playtime DESC LIMIT 1;

SELECT product_id FROM `headphones` WHERE connectivity = 'wireless' 
AND headphone_type = 'on-ear' 
ORDER BY playtime DESC LIMIT 1;

SELECT product_id FROM `headphones` WHERE brand IN ('Avantree', 'Sennheiser') 
AND headphone_type = 'over-ear' 
ORDER BY playtime DESC LIMIT 1;

SELECT product_id FROM `headphones` WHERE playtime > 20 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `headphones` WHERE connectivity = 'wireless' AND (headphone_type = 'in-ear' OR headphone_type = 'earbud') 
ORDER BY playtime DESC 
LIMIT 1;

SELECT product_id FROM `headphones` WHERE (brand = 'Phaiser' OR brand = 'JVC') 
AND playtime >= 15 
ORDER BY price DESC;

SELECT product_id FROM `headphones` WHERE brand <> 'Toysdone' AND playtime >= 24 
ORDER BY product_id 
LIMIT 1;

SELECT product_id FROM `headphones` WHERE headphone_type = 'in-ear'
ORDER BY price, playtime DESC
LIMIT 1;

SELECT product_id FROM `headphones` WHERE playtime > (SELECT AVG(playtime) FROM headphones)
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `headphones` WHERE brand = 'ABCShopUSA' 
ORDER BY playtime DESC 
LIMIT 1;

SELECT product_id FROM `headphones` WHERE connectivity = 'wireless' AND headphone_type = 'in-ear' 
ORDER BY playtime DESC 
LIMIT 1;
