SELECT product_id FROM `space_heaters` WHERE brand IN ('Iliving', 'Bionaire') 
ORDER BY product_id 
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE heating_efficiency = 'Average' 
AND warranty > 1 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE brand IN ('Vornado', 'Honeywell') 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE warranty = (SELECT warranty FROM space_heaters WHERE brand IN ('Crane USA', 'Dyson') ORDER BY warranty DESC LIMIT 1)
AND brand IN ('Crane USA', 'Dyson')
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE price <= 200 AND brand <> 'Honeywell' 
ORDER BY heating_efficiency DESC, warranty DESC 
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE brand IN ("Holmes", "Vornado") 
ORDER BY warranty DESC 
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE brand IN ('Lasko', 'Dyson') 
ORDER BY warranty DESC 
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE brand IN ('Lasko', 'Impress') AND heating_efficiency = 'Fast'
ORDER BY price DESC
LIMIT 1;

SELECT product_id FROM `space_heaters` WHERE brand IN ('Holmes', 'Dyson') 
ORDER BY price ASC 
LIMIT 1;

